import "./App.css";
import { useState } from "react";
import {axios} from "axios";

function App() {
  const [currentURL, setCurrentURL] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("https://example.con/login", { "data":currentURL }).then((response) => {
      console.log(response);
    });
  };

  return (
    <div className="App">
      <div className="center">
        <h1 align="center"> URL Shortener </h1>
      </div>
      <div>
        <input id="url" type="text" placeholder="Enter URL" name="url" value={currentURL} onChange={(e)=>{setCurrentURL(e.target.value)}} />
        <br />
        <input id="submit" type="submit" className="btn" onClick={handleSubmit} />
      </div>
    </div>
  );
}

export default App;
