import "./App.css";
import { useState } from "react";

function App() {
  const [currentURL, setCurrentURL] = useState("");
  const [shortURL, setShortURL] = useState("");
  const [done,setDone]=useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    const url = "http://localhost:4010/shorten";

    const data = {
      currentURL,
    };

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    };

    fetch(url, options)
      .then((response) => {
        //display the shorted url
        console.log(response);
        return response.json();
      })
      .then((data) => {
        //display the shorted url
        console.log(data);
        setShortURL(data.shortedurl);
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
      });
  };

  return (
    <div className="App">
      <div className="center">
        <h1 align="center"> URL Shortener </h1>
      </div>
      <div>
        <form action="" onSubmit={handleSubmit}>
          <input
            id="url"
            type="text"
            placeholder="Enter URL"
            name="url"
            value={currentURL}
            onChange={(e) => {
              setCurrentURL(e.target.value);
            }}
          />
          <br />
          <input id="submit" type="submit" className="btn" />
        </form>
      </div><br />
      <div >
        <div id="shortcode" className="mydiv">Shorted URL  :   <a href="">http://localhost:3000/{shortURL}</a></div>
      </div>
    </div>
  );
}

export default App;
